public void readJTable() {
   
        DefaultTableModel modelo = (DefaultTableModel) TABPacientes.getModel();
        modelo.setNumRows(0);
        PacienteDAO pdao = new PacienteDAO();

        for (Paciente p : pdao.read()) {

            modelo.addRow(new Object[]{
                p.getCpf(),
                p.getNome(),
                p.getEmail(),
                p.getTelefone(),
                p.getSexo(),
                p.getEstado_civil(),
                p.getData_nascimento(),
                p.getNumero_endereco(),
                p.getCep(),
                p.getRua(),
                p.getBairro(),
                p.getCidade(),
                p.getEstado()
               
                 
               
            });

        }

    }

public void readJTableForDesc(String desc) {
        
        DefaultTableModel modelo = (DefaultTableModel) TABPacientes.getModel();
        modelo.setNumRows(0);
        PacienteDAO pdao = new PacienteDAO();

        for (Paciente p : pdao.readForDesc(desc)) {

            
            modelo.addRow(new Object[]{
                p.getCpf(),
                p.getNome(),
                p.getEmail(),
                p.getTelefone(),
                p.getSexo(),
                p.getEstado_civil(),
                p.getData_nascimento(),
                p.getNumero_endereco(),
                p.getCep(),
                p.getRua(),
                p.getBairro(),
                p.getCidade(),
                p.getEstado() 
            });

        }

    }



