    private void BCadastrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BCadastrarActionPerformed
        
           if(TBNumero.getText().isEmpty()){
            TBNumero.setText("0");
         
         
         }
        if(TBCPF.getText().isEmpty()||TBNome.getText().isEmpty()){

            JOptionPane.showMessageDialog(null,"Preencher todos os campos obrigatórios");

        }
       // if(TBNumero.getText().isEmpty()){
            
         // TBNumero.setText("0");
            
   //    }
        else{
            Paciente p = new Paciente();
            PacienteDAO pdao = new PacienteDAO();
            Consulta c = new Consulta();
            ConsultaDAO cdao= new ConsultaDAO();

            p.setCpf(TBCPF.getText());
            p.setNome(TBNome.getText());
            p.setEmail(TBEmail.getText());
            p.setTelefone(TBTelefone.getText());
            p.setSexo(CBSexo.getSelectedItem().toString());
            p.setEstado_civil(CBEstadoCivil.getSelectedItem().toString());
            p.setData_nascimento( TBDataNascimento.getText());
            p.setNumero_endereco(Integer.parseInt(TBNumero.getText()));
            p.setCep(TBCEP.getText());
            p.setRua(TBRua.getText());
            p.setEstado(CBEstado.getSelectedItem().toString());
            p.setBairro(TBBairro.getText());
            p.setCidade(CBCidade.getSelectedItem().toString());
            
            pdao.createPaciente(p);
            TBCPF.setText("");
            TBNome.setText("");
            TBEmail.setText("");
            TBTelefone.setText("");
            TBDataNascimento.setText("");
            TBNumero.setText("");
            TBCEP.setText("");
            TBRua.setText("");
            TBBairro.setText("");
  
            readJTable();
              
        }
             
    }//GEN-LAST:event_BCadastrarActionPerformed

    private void BAlterarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BAlterarActionPerformed
        if (TABPacientes.getSelectedRow() != - 1) {
            int confirma = JOptionPane.showConfirmDialog(null,"Confirma a alteração deste Paciente? ","Atenção",JOptionPane.YES_NO_OPTION);
        if (confirma== JOptionPane.YES_OPTION){
               if(TBNumero.getText().isEmpty()){
            TBNumero.setText("0");
         
         
         }
            if(TBCPF.getText().isEmpty()||TBNome.getText().isEmpty()){

                JOptionPane.showMessageDialog(null,"Preencher todos os campos obrigatórios");

            }
   
            else{

            Paciente p = new Paciente();
            PacienteDAO pdao = new PacienteDAO();

                p.setNome(TBNome.getText());
                p.setEmail(TBEmail.getText());
                p.setTelefone(TBTelefone.getText());
                p.setSexo(CBSexo.getSelectedItem().toString());
                p.setEstado_civil(CBEstadoCivil.getSelectedItem().toString());
                p.setData_nascimento( TBDataNascimento.getText());
                p.setNumero_endereco(Integer.parseInt(TBNumero.getText()));
                p.setCep(TBCEP.getText());
                p.setRua(TBRua.getText());
                p.setEstado(CBEstado.getSelectedItem().toString());
                p.setBairro(TBBairro.getText());
                p.setCidade(CBCidade.getSelectedItem().toString());
                p.setCpf((String)TABPacientes.getValueAt(TABPacientes.getSelectedRow(), 0));

                pdao.update(p);
                
                TBCPF.setText("");
                TBNome.setText("");
                TBEmail.setText("");
                TBDataNascimento.setText("");
                TBTelefone.setText(""); 
                TBNumero.setText("");
                TBCEP.setText("");
                TBRua.setText("");
                TBBairro.setText("");
                
                readJTable();
            }

        }

        else{
        
        
        }
         
        }        
        else {
            JOptionPane.showMessageDialog(null, "Selecione um Paciente para alterar");

        }
        
       
        
        
    }//GEN-LAST:event_BAlterarActionPerformed

    private void BExcluirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BExcluirActionPerformed
        if (TABPacientes.getSelectedRow() != -1) {
           int confirma = JOptionPane.showConfirmDialog(null,"Confirma a exclusão deste Paciente? ","Atenção",JOptionPane.YES_NO_OPTION);
        if (confirma== JOptionPane.YES_OPTION){
            Paciente p = new Paciente();
            PacienteDAO pdao = new PacienteDAO();

            p.setCpf((String) TABPacientes.getValueAt(TABPacientes.getSelectedRow(), 0));

            pdao.delete(p);
             TBCPF.setText("");
             TBNome.setText("");
             TBEmail.setText("");
             TBDataNascimento.setText("");
             TBTelefone.setText("");
             TBNumero.setText("");
             TBCEP.setText("");
             TBRua.setText("");
             TBBairro.setText("");
            readJTable();

        } 
        else{
        
        
        }   
        }
        else {
            JOptionPane.showMessageDialog(null, "Selecione um Paciente para excluir.");
        }
        
     
    }//GEN-LAST:event_BExcluirActionPerformed

